package com.youngbin.sample;import android.content.res.Resources;import android.util.DisplayMetrics;public class ScreenUtil {    /**     * 获取屏幕尺寸     */    public static DisplayMetrics getDisplayMetrics() {        return Resources.getSystem().getDisplayMetrics();    }    /**     * 获取屏幕密度     */    public static float getDensity() {        return getDisplayMetrics().density;    }    /**     * 根据手机的分辨率从 dp 的单位 转成为 px(像素)     */    public static int dip2px(float value) {        return (int) (value * getDensity() + 0.5f);    }    /**     * 根据手机的分辨率从 px(像素) 的单位 转成为 dp     */    public static int px2dip(float value) {        return (int) (value / getDensity() + 0.5f);    }}